package kg.example.hm_07_m03

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.LinearLayoutManager
import kg.example.hm_07_m03.databinding.FragmentProductListBinding


class ProductListFragment : Fragment() {


           private lateinit var binding: FragmentProductListBinding

        override fun onCreateView(
            inflater: LayoutInflater, container: ViewGroup?,
            savedInstanceState: Bundle?
        ): View {
            binding = FragmentProductListBinding.inflate(inflater, container, false)
            return binding.root
        }

        override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
            super.onViewCreated(view, savedInstanceState)

            val products = listOf(
                Product("BMW", listOf(
                    Model("X1", "Details about X1", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGUvQAUHkoVNNGnLFD5tVVuLsSkt-RQ5I1yg&s"),
                    Model("X2", "Details about X2", "data:image/jpeg;base64,")
                    Model("X3", "Details about X3", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQjyNTokWAbnOYjq5uw_I8stK12t11sQsa-2Q&s")
                    Model("X4", "Details about X4", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSadeDveVD-SWXyoeTo6r6SHuW9jbnzIhsWoQ&s")
                    Model("X5", "Details about X5", "https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/BMW_X5_%28E53%2C_Facelift%29_%E2%80%93_Frontansicht%2C_12._Juni_2011%2C_D%C3%BCsseldorf.jpg/1200px-BMW_X5_%28E53%2C_Facelift%29_%E2%80%93_Frontansicht%2C_12._Juni_2011%2C_D%C3%BCsseldorf.jpg")
                ))
            )

            binding.rvProduct.layoutManager = LinearLayoutManager(requireContext())
            binding.rvProduct.adapter = ProductAdapter(products) { product ->
                // Handle product click
                val fragment = ModelListFragment().apply {
                    arguments = Bundle().apply {
                        putParcelableArrayList("models", ArrayList(product.models))
                    }
                }
                requireActivity().supportFragmentManager.beginTransaction()
                    .replace(R.id.fragment_container, fragment)
                    .addToBackStack(null)
                    .commit()
            }
        }



}